import React from "react";
//import PropTypes from "prop-types";

export const AddStudent = props => {
  return (
    <div>
      <h2>Add new student</h2>
      <form onSubmit={props.onAddStudent}>
        <input
          type="text"
          name="newStudent"
          value={props.newStudent}
          onChange={props.onEnterName}
        />
        <input type="submit" value="submit" />
      </form>
    </div>
  );
};

export const displayOptions = {
  select: (
    <SelectStudent
      selectStudent={this.props.func.onSelectStudent}
      student={this.props.student}
    />
  ),
  stats: <Stats student={this.props.student} />,
  edit: (
    <EditStudent
      editStudent={this.props.func.onEditStudent}
      student={this.props.student}
    />
  ),
  delete: (
    <DeleteStudent
      deleteStudent={this.props.func.onDeleteStudent}
      student={this.props.student}
    />
  )
};

export const onDisplayStudent = (choice, props) => {
  return displayOptions(choice);
};

const DeleteStudent = props => {
  return <div>Do you wish to delete ${props.student.name}?</div>;
};

const EditStudent = props => {};

const SelectStudent = props => {};

const Stats = props => {
  const boards = props.student.boardsCompleted;
  const allBoardsCompleted = boards
    ? "No boards completed!"
    : boards.map(byDate => {
        return (
          <p>
            ${byDate.day}: ${byDate.boardsCompleted}
          </p>
        );
      });

  return (
    <div className="stats">
      <h2 class="student_stats">{props.student.name}</h2>
      <div>{allBoardsCompleted}</div>
    </div>
  );
};
