import React, { Component } from "react";
// import { Link } from "react-router-dom";
import { StudentExpand } from "./StudentOptions";
// import Stats from "./Stats";
// import EditForm from "./EditForm";
// import PropTypes from "prop-types";
// import CustomerStyles from './Customer.css';

class Student extends Component {
  constructor(props) {
    super(props);

    this.state = {
      onRedirect: "allStudents",
      key: "default"
    };
  }

  add = state => {
    this.setState(state => ({ key: "add" }));
  };

  del = state => {
    this.setState(state => ({ key: "del" }));
  };

  edit = state => {
    this.setState(state => ({ key: "edit" }));
  };

  select = state => {
    this.setState(state => ({ key: "select" }));
  };

  stats = state => {
    this.setState(state => ({ key: "stats" }));
  };

  render() {
    return (
      <div className="student">
        {/* <span className="student__content"> */}
        <div className="student__content-name">{this.props.student.name}</div>
        {/* </span> */}
        <StudentExpand
          key={this.state.key}
          student={this.props.student}
          func={this.props.func}
        />
        >
        <div>
          <span className="nav-item px-5">
            <button
              onClick={this.select}
              className="student__select btn btn-primary"
            >
              Select
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.stats}
              className="student__stats btn btn-primary"
            >
              History
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.edit}
              className="student__edit btn btn-primary"
            >
              Edit Name
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.del}
              className="student__delete btn btn-primary"
            >
              Delete Student
            </button>
          </span>
        </div>
      </div>
    );
  }
}

// Student.propTypes = {
//   id: PropTypes.number,
//   name: PropTypes.string,
//   studentNameCallback: PropTypes.func
// };

export default Student;
