import React, { Component } from "react";
import { Link } from "react-router-dom";
// import { StudentExpand } from "./StudentOptions";
// import Add from "./Add";
import * as API from "../helpers/API";
import Delete from "./Delete";
import Edit from "./Edit";
import Select from "./Select";
import Stats from "./Stats";
// import EditForm from "./EditForm";
// import PropTypes from "prop-types";
// import CustomerStyles from './Customer.css';

class Student extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // onRedirect: "allStudents",
      detail: "blank",
      newName: "New name"
    };
  }

  // EDIT NAME
  onChangeName = event => {
    const name = event.target.value.trim();
    console.log(name);
    this.setState(state => ({ newName: name }));
  };

  onEditStudentName = event => {
    event.preventDefault();
    const student = Object.assign({}, this.props.student, {
      name: this.state.newName
    });
    console.log(student);
    debugger;
    API.updateStudent(student._id, student).then(
      console.log(`Current student = ${this.state.student.name}`)
    );
    debugger;
  };

  // DELETE NAME
  onDeleteStudent = () => {
    API.deleteStudent({ id: this.props.student._id })
      .then(console.log(`Student ${this.props.student.name} deleted`))
      // .then(
      //   this.setState(state => ({
      //     student: {
      //       _id: "",
      //       name: "",
      //       teacher: "",
      //       boardsCompleted: []
      //     }}))
      .then(this.returnToList());
  };

  // CONDITIONAL RENDERING

  add = state => {
    this.setState(state => ({ detail: "add" }));
  };

  del = state => {
    this.setState(state => ({ detail: "del" }));
  };

  edit = state => {
    this.setState(state => ({ detail: "edit" }));
  };

  select = state => {
    this.setState(state => ({ detail: "select" }));
  };

  stats = state => {
    this.setState(state => ({ detail: "stats" }));
  };

  onDisplay = (state, props) => {
    switch (state) {
      case "blank":
        return "";
      case "del":
        return (
          <Delete
            selectStudent={this.props.func.select}
            deleteStudent={this.onDeleteStudent}
            student={this.props.student}
          />
        );
      case "edit":
        return (
          <Edit
            selectStudent={this.props.func.onSelectStudent}
            editStudent={this.props.func.onEditStudentName}
            returnToList={this.props.func.returnToList}
            changeName={this.onChangeName}
            student={this.props.student}
            name={this.state.name}
          />
        );
      case "select":
        return (
          <Select
            selectStudent={this.props.func.onSelectStudent}
            student={this.props.student}
          />
        );
      case "stats":
        return (
          <Stats
            selectStudent={this.props.func.onSelectStudent}
            student={this.props.student}
          />
        );
      default:
        return "";
    }
  };

  studentOption = (state, props) => {
    if (this.state.detail) {
      return this.onDisplay(this.state.detail, this.props);
    } else {
      return "";
    }
  };

  // RENDER COMPONENT

  render() {
    return (
      <div className="student">
        {/* <span className="student__content"> */}
        <div className="student__content-name">{this.props.student.name}</div>
        {/* </span> */}
        <div>{this.studentOption()}</div>
        <div>
          <span className="nav-item px-5">
            <button
              onClick={this.select}
              className="student__select btn btn-primary"
            >
              Select
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.stats}
              className="student__stats btn btn-primary"
            >
              History
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.edit}
              className="student__edit btn btn-primary"
            >
              Edit Name
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.del}
              className="student__delete btn btn-primary"
            >
              Delete Student
            </button>
          </span>
        </div>
      </div>
    );
  }
}

// Student.propTypes = {
//   id: PropTypes.number,
//   name: PropTypes.string,
//   studentNameCallback: PropTypes.func
// };

export default Student;
