import React from "react";
import { format } from "date-fns";
//import PropTypes from "prop-types";

const Stats = props => {
  const allBoardsCompleted = boards => {
    return boards
      ? boards.map(byDate => {
          const day = format(byDate.day, "MM/DD/YYYY");
          return (
            <p>
              {day}: {byDate.boardsCompleted} boards completed
            </p>
          );
        })
      : "No boards completed!";
  };

  return (
    <div className="stats">
      <p className="student_stats">{props.student.name}</p>
      <div>{allBoardsCompleted(props.student.boardsCompleted)}</div>
      <button onClick={props.cancel}>Return</button>
    </div>
  );
};

export default Stats;
