import React, { Component } from "react";
import { Route, Link } from "react-router-dom";
import { withRouter } from "react-router";
import logo from "../logo.svg";
import "../App.css";
import * as API from "../helpers/API";
// import UI from "../helpers/UI";
import Navbar from "../Navbar";
import SelectedInfo from "./SelectedInfo";
import StudentList from "./StudentList";
import StickerBoard from "./StickerBoard.js";
import isSameDay from "date-fns/is_same_day";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      student: {
        _id: "",
        name: "",
        teacher: "",
        boardsCompleted: []
      },
      pageAnimation: true,
      pageSound: true,
      // does this go in StickerBoard?
      stickersPlaced: 0
    };
  }

  onToggleAnimation() {
    this.setState({
      pageAnimation: !this.state.pageAnimation
    });
  }

  onToggleSound = () => {
    this.setState({
      pageSound: !this.state.pageSound
    });
  };

  onAddStudent = name => {
    const student = Object.assign({}, this.state.student, { name: name });
    this.setState({ student: student });
    API.addStudent(this.state.student.name).then(
      console.log(`Current Student = ${this.state.student.name}`)
    );
    // mount all students? studentlist?
  };

  onDeleteStudent = () => {
    API.deleteStudent(this.state.student._id)
      .then(console.log(`Student ${this.state.student.name} deleted`))
      .then(
        this.setState({
          student: {
            _id: "",
            name: "",
            teacher: "",
            boardsCompleted: []
          }
        })
      );
  };

  onEditStudentName = name => {
    const student = Object.assign({}, this.state.student, { name: name });
    API.updateStudent(student).then(
      console.log(`Current student = ${this.state.student.name}`)
    );
  };

  onSelectStudent = () => {
    API.loadStudent(this.state.student._id).then(
      console.log(`Current student = ${this.state.student.name}`)
    );
  };

  onPlaceSticker() {
    // UI.changeBoard();
    this.setState({ stickersPlaced: this.state.stickersPlaced + 1 });
    if (this.state.stickersPlaced === 3) {
      this.addBoardToStats();
      // UI.celebrate();
      this.setState({ stickersPlaced: 0 });
      API.updateStudent(this.state.student).then(
        console.log(`Boards completed = ${this.state.student.boardsCompleted}`)
      );
    }
  }

  addBoardToStats() {
    const student = Object.assign({}, this.state.student);
    const currentBoard = student.boardsCompleted[-1];
    const today = Date.now();
    if (isSameDay(currentBoard.day, today)) {
      currentBoard.boardsCompleted++;
    } else {
      student.boardsCompleted.push({ day: today, boardsCompleted: 1 });
    }
    this.setState({ student: student });
  }

  studentFunctions = [
    this.onAddStudent,
    this.onDeleteStudent,
    this.onEditStudentName,
    this.onSelectStudent
  ];

  render() {
    return (
      <div>
        <header className="App-header">
          <img
            src={
              "https://i.ibb.co/6PZqtHk/Screen-Shot-2019-07-19-at-11-22-08-AM.png"
            }
            className="App-logo"
            alt="chalkboard"
          />
          <h1 className="App-title">
            <img
              src={
                "https://i.ibb.co/TvRxPGd/eb822bd15530836fa9508123006bd24a.png"
              }
              className="App-title"
              alt="Sticker Star"
            />
          </h1>
          <SelectedInfo student={this.state.student} />
        </header>
        <Navbar
          onLogout={this.props.onLogout}
          onToggleAnimation={this.props.onToggleAnimation}
          onToggleSound={this.props.onToggleSound}
          pageAnimation={this.state.pageAnimation}
          pageSound={this.state.pageSound}
        />
        <div>
          <Route
            exact
            path="/home"
            // tag for default?
            render={routerprops => (
              <StudentList
                {...routerprops}
                studentFunctions={this.studentFunctions}
                student={this.state.student}
                // movieTitleCallbackinMovieList={this.movieToRent}
              />
            )}
          />
          <Route
            exact
            path="/board"
            render={routerprops => (
              <StickerBoard
                {...routerprops}
                onPlaceSticker={this.props.onPlaceSticker}
                stickersPlaced={this.state.stickersPlaced}
                student={this.state.student}
                // customerNameCallbackCustomers={this.customerRenting}
              />
            )}
          />
        </div>
      </div>
    );
  }
}

export default withRouter(Home);
