import React, { Component } from "react";
import { Link } from "react-router-dom";
import {
  EditStudent,
  DeleteStudent,
  SelectStudent,
  Stats
} from "./StudentOptions";
// import EditForm from "./EditForm";
// import PropTypes from "prop-types";
// import CustomerStyles from './Customer.css';

class Student extends Component {
  constructor(props) {
    super(props);

    this.state = {
      onRedirect: "allStudents",
      pathname: `/student/${this.props.student._id}`
    };
  }

  options = () => {
    const path = this.state.pathname;
    if (path === "/select") {
      return (
        <SelectStudent
          selectStudent={this.props.func.onSelectStudent}
          student={this.props.student}
        />
      );
    } else if (path === "/history") {
      return <Stats student={this.props.student} />;
    } else if (path === "/edit") {
      return (
        <EditStudent
          editStudent={this.props.func.onEditStudent}
          student={this.props.student}
        />
      );
    } else if (path === "/delete") {
      return (
        <DeleteStudent
          deleteStudent={this.props.func.onDeleteStudent}
          student={this.props.student}
        />
      );
    }
  };

  render() {
    return (
      <div className="student">
        {/* <span className="student__content"> */}
        <div className="student__content-name">{this.props.student.name}</div>
        {/* </span> */}
        <div>{this.options}</div>
        <div>
          <span className="nav-item px-5">
            <Link to="/select" className="student__select btn btn-primary">
              Select
            </Link>
          </span>
          <span className="nav-item px-5">
            <Link to="/history" className="student__stats btn btn-primary">
              History
            </Link>
          </span>
          <span className="nav-item px-5">
            <Link to="/edit" className="student__edit btn btn-primary">
              Edit Name
            </Link>
          </span>
          <span className="nav-item px-5">
            <Link to="/delete" className="student__delete btn btn-primary">
              Delete Student
            </Link>
          </span>
        </div>
      </div>
    );
  }
}

// Student.propTypes = {
//   id: PropTypes.number,
//   name: PropTypes.string,
//   studentNameCallback: PropTypes.func
// };

export default Student;
