import React, { Component } from "react";
// import { Link } from "react-router-dom";
// import { StudentExpand } from "./StudentOptions";
// import Add from "./Add";
import Del from "./Del";
import Edit from "./Edit";
import Select from "./Select";
import Stats from "./Stats";
// import EditForm from "./EditForm";
// import PropTypes from "prop-types";
// import CustomerStyles from './Customer.css';

class Student extends Component {
  constructor(props) {
    super(props);

    this.state = {
      // onRedirect: "allStudents",
      display: null
    };
  }

  add = state => {
    this.setState(state => ({ display: "add" }));
  };

  del = state => {
    this.setState(state => ({ display: "del" }));
  };

  edit = state => {
    this.setState(state => ({ display: "edit" }));
  };

  select = state => {
    this.setState(state => ({ display: "select" }));
  };

  stats = state => {
    this.setState(state => ({ display: "stats" }));
  };

  onDisplay = (state, props) => {
    switch (this.state.display) {
      case "del":
        return (
          <Del
            deleteStudent={this.props.func.onDeleteStudent}
            student={this.props.student}
          />
        );
      case "edit":
        return (
          <Edit
            editStudent={this.props.func.onEditStudent}
            student={this.props.student}
          />
        );
      case "select":
        return (
          <Select
            selectStudent={this.props.func.onSelectStudent}
            student={this.props.student}
          />
        );
      case "stats":
        return <Stats student={this.props.student} />;
      default:
        return null;
    }
  };

  studentOption = this.onDisplay(this.state, this.props);

  render() {
    return (
      <div className="student">
        {/* <span className="student__content"> */}
        <div className="student__content-name">{this.props.student.name}</div>
        {/* </span> */}
        {this.studentOption}
        <div>
          <span className="nav-item px-5">
            <button
              onClick={this.select}
              className="student__select btn btn-primary"
            >
              Select
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.stats}
              className="student__stats btn btn-primary"
            >
              History
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.edit}
              className="student__edit btn btn-primary"
            >
              Edit Name
            </button>
          </span>
          <span className="nav-item px-5">
            <button
              onClick={this.del}
              className="student__delete btn btn-primary"
            >
              Delete Student
            </button>
          </span>
        </div>
      </div>
    );
  }
}

// Student.propTypes = {
//   id: PropTypes.number,
//   name: PropTypes.string,
//   studentNameCallback: PropTypes.func
// };

export default Student;
