import React from "react";
//import PropTypes from "prop-types";

const func = props.studentFunctions;

export const AddStudent = props => {
  return (
    <div>
      <h2>Add new student</h2>
      <form onSubmit={func.onAddStudent}>
        <input
          type="text"
          name="newStudent"
          value={props.student.name}
          // onChange={props.}
        />
        <input type="submit" value="submit" />
      </form>
    </div>
  );
};

export const DeleteStudent = props => {
  return <div>Do you wish to delete ${props.student.name}?</div>;
};

export const EditStudent = props => {};

export const SelectStudent = props => {};

export const Stats = props => {
  const boards = props.student.boardsCompleted;
  const allBoardsCompleted = boards
    ? "No boards completed!"
    : boards.map(byDate => {
        return (
          <p>
            ${byDate.day}: ${byDate.boardsCompleted}
          </p>
        );
      });

  return (
    <div className="stats">
      <h2 class="student_stats">{props.student.name}</h2>
      <div>{allBoardsCompleted}</div>
    </div>
  );
};
