import React, { Component } from "react";
// import PropTypes from "prop-types";
// import API from "../helpers/API.js";
import Sticker from "./Sticker";
import Stats from "./Stats";
// import Animation from "./Animation";
import "./sticker.css";

class StickerBoard extends Component {
  render() {
    const placedStickers = this.props.stickers.map((sticker, idx) => {
      return (
        <div key={idx}>
          <Sticker
            alt={this.props.sticker.alt}
            src={this.props.sticker.src}
            onClick={this.props.onPlaceSticker}
          />
        </div>
      );
    });

    return (
      <div>
        <h3>Hi {this.props.student.name}!</h3>
        <div className="temporary">
          <button onClick={this.props.onPlaceSticker}>Place Sticker</button>
          <p>Stickers Placed: {this.props.stickersPlaced}</p>
          <Stats student={this.props.student} />
          <img src={this.props.reward.src} alt={this.props.reward.alt} />>
          {placedStickers}
        </div>
        <div className="stickerBoard">
          <div className="stickers">
            <Sticker stickerID="1" placeSticker={this.placeSticker} />
            <Sticker stickerID="2" placeSticker={this.placeSticker} />
            <Sticker stickerID="3" placeSticker={this.placeSticker} />
          </div>
          <div className="reward">
            {/* "I'm working for... (ribbon default image)" */}
          </div>
        </div>
      </div>
    );
  }
}

export default StickerBoard;
