// Page provides info on students belonging to teacher, and options to CRUD students
// GETS all <Student>s with teacherName matching logged in teacher
// Maps all <Student>s into list of components
// Passes up props of selected student to <Home>, for SelectedInfo
//

import React, { Component } from "react";
import PropTypes from "prop-types";
import * as API from "../helpers/API";
import { withRouter } from "react-router";
import { AddStudent } from "./StudentOptions";
import axios from "axios";
import Student from "./Student";
// const { Student } = require("./Student");
// import './CustomerList.css';

class StudentList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      newStudent: "Enter name",
      students: []
    };
  }

  getStudents = () => {
    axios
      .get("/students")
      .then(res => {
        console.log(res);
        this.setState(state => ({ students: res.data }));
      })
      .catch(err => {
        console.log(err);
      });
  };

  componentDidMount() {
    this.getStudents();
  }

  // componentDidUpdate() {
  //   this.getStudents();
  // }

  onEnterName = event => {
    const name = event.target.value.trim();
    this.setState(state => ({ newStudent: name }));
  };

  onAddStudent = event => {
    event.preventDefault();
    if (this.state.newStudent) {
      const student = {
        name: this.state.newStudent,
        teacher: this.props.user.name
      };
      console.log(this.state.newStudent);
      API.addStudent(student)
        .then(console.log(`Current Student = ${student.name}`))
        .then(this.returnToList(event));
    } else {
      alert("Please enter student name");
    }
  };

  // studentNameCallback = student => {
  //   this.props.studentNameCallback(student);
  // };

  render(routerprops) {
    const allStudents = this.state.students.map(student => {
      console.log(student);
      return (
        <div key={student._id}>
          <Student
            // tag={this.props.tag}
            student={student}
            func={this.props.studentFunctions}
            getStudents={this.getStudents}
            // studentNameCallback={() => this.studentNameCallback(student)}
          />
        </div>
      );
    });

    return this.state.students.length > 0 ? (
      <div>
        <div>
          {/* <AddStudent
            onAddStudent={this.onAddStudent}
            onEnterName={this.onEnterName}
            newStudent={this.state.newStudent}
            className="student_add btn btn-primary"
          /> */}
          {/* Add Student
        </button> */}
        </div>
        <div className="students">{allStudents}</div>
      </div>
    ) : (
      <div>"You have no students!"</div>
    );
  }
}

StudentList.propTypes = {
  customerNameCallbackCustomers: PropTypes.func
};

export default withRouter(StudentList);
