import React, { Component } from "react";
import { Route, Redirect } from "react-router-dom";
import { withRouter } from "react-router";
// import * as API from "./helpers/API";
import Home from "./components/Home";
import Login from "./components/Login";
import "./App.css";
//import PropTypes from "prop-types";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      user: {
        name: "dongo",
        // email: "dongo@dongo.com",
        password: "dongo"
        // passwordConfirmation: "dongo"
      }
    };
  }

  onLogin = user => {
    this.setState({ isLoggedIn: true, user: user }).then(
      console.log(`${this.state.user.name} logged in`)
    );
  };

  onLogout = () => {
    this.setState({
      isLoggedIn: false,
      user: {
        name: "",
        password: ""
      }
    }).then(console.log("User logged out"));
  };

  render() {
    return (
      <div className="App">
        <Route
          exact
          path="/"
          render={() =>
            this.state.isLoggedIn ? (
              <Redirect to="/home" />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
        <Route
          path="/home"
          render={() =>
            this.state.isLoggedIn ? (
              <Home onLogout={this.onLogout} user={this.state.user} />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
        <Route
          exact
          path="/login"
          render={() =>
            !this.state.isLoggedIn ? (
              <Login loggedIn={this.state.loggedIn} onLogin={this.onLogin} />
            ) : (
              <Redirect to="/home" />
            )
          }
        />
      </div>
    );
  }
}

export default withRouter(App);
