  issuer: "https://dev-134015.okta.com/oauth2/default",
  redirect_uri: window.location.origin + "/implicit/callback",
  client_id: "0oa10v39pl0FD32AA357"
};
//import PropTypes from "prop-types";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: true,
      students: [],
      tag: "",
      user: {
        name: "dongo",
        email: "dongo@dongo.com",
        password: "dongo",
        passwordConfirmation: "dongo"
      }
    };
  }

  // onLogin = user => {
