import React, { Component } from "react";
import { Route } from "react-router-dom";
import { withRouter } from "react-router";
import "../App.css";
import * as API from "../helpers/API";
import axios from "axios";
import Navbar from "../Navbar";
import SelectedInfo from "./SelectedInfo";
import StudentList from "./StudentList";
import StickerBoard from "./StickerBoard";
import isSameDay from "date-fns/is_same_day";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      student: {
        _id: "",
        name: "",
        teacher: "",
        boardsCompleted: []
      },
      pageAnimation: true,
      pageSound: true,
      // does this go in StickerBoard?
      stickersPlaced: 0
    };
  }

  onToggleAnimation = () => {
    this.setState(state => ({
      pageAnimation: !this.state.pageAnimation
    }));
    console.log(this.state.pageAnimation);
  };

  onToggleSound = () => {
    this.setState(state => ({
      pageSound: !this.state.pageSound
    }));
    console.log(this.state.pageSound);
  };

  returnToList = () => {
    this.props.history.push("/home");
  };

  onDeleteStudent = id => {
    API.deleteStudent(id)
      .then(console.log(`Student ${this.state.student.name} deleted`))
      .then(
        this.setState(state => ({
          student: {
            _id: "",
            name: "",
            teacher: "",
            boardsCompleted: []
          }
        })).then(this.returnToList())
      );
  };

  onSelectStudent = id => {
    // API.loadStudent(this.state.student._id).then(
    //   console.log(`Current student = ${this.state.student.name}`)
    // );
    axios
      .get("/students/" + id)
      .then(res => {
        this.setState(state => ({
          stickersPlaced: 0,
          studentName: res.data.student.name,
          teacherName: res.data.student.teacher,
          boardsCompleted: res.data.student.boardsCompletedByDate,
          studentID: res.data.student._id
        }));
      })
      .catch(err => console.log(err));
  };

  onPlaceSticker() {
    // UI.changeBoard();
    this.setState(state => ({ stickersPlaced: this.state.stickersPlaced + 1 }));
    if (this.state.stickersPlaced === 3) {
      this.addBoardToStats();
      // UI.celebrate();
      this.setState(state => ({ stickersPlaced: 0 }));
      API.updateStudent(this.state.student).then(
        console.log(`Boards completed = ${this.state.student.boardsCompleted}`)
      );
    }
  }

  addBoardToStats() {
    const student = Object.assign({}, this.state.student);
    const currentBoard = student.boardsCompleted[-1];
    const today = Date.now();
    if (isSameDay(currentBoard.day, today)) {
      currentBoard.boardsCompleted++;
    } else {
      student.boardsCompleted.push({ day: today, boardsCompleted: 1 });
    }
    this.setState({ student: student });
  }

  studentFunctions = {
    add: this.onAddStudent,
    del: this.onDeleteStudent,
    edit: this.onEditStudentName,
    select: this.onSelectStudent,
    return: this.returnToList
  };

  render() {
    return (
      <div>
        <header className="App-header">
          <img
            src={
              "https://i.ibb.co/6PZqtHk/Screen-Shot-2019-07-19-at-11-22-08-AM.png"
            }
            className="App-logo"
            alt="chalkboard"
          />
          <h1 className="App-title">
            <img
              src={
                "https://i.ibb.co/TvRxPGd/eb822bd15530836fa9508123006bd24a.png"
              }
              className="App-title"
              alt="Sticker Star"
            />
          </h1>
          <SelectedInfo
            student={this.state.student}
            teacher={this.props.user}
          />
        </header>
        <Navbar
          onLogout={this.onLogout}
          onToggleAnimation={this.onToggleAnimation}
          onToggleSound={this.onToggleSound}
          pageAnimation={this.state.pageAnimation}
          pageSound={this.state.pageSound}
        />
        <div>
          <Route
            exact
            path="/home"
            // tag for default?
            render={routerprops => (
              <StudentList
                {...routerprops}
                tag={this.props.tag}
                studentFunctions={this.studentFunctions}
                student={this.state.student}
                // students={this.props.students}
                // movieTitleCallbackinMovieList={this.movieToRent}
              />
            )}
          />
          <Route
            exact
            path="/board"
            render={routerprops => (
              <StickerBoard
                {...routerprops}
                onPlaceSticker={this.onPlaceSticker}
                stickersPlaced={this.state.stickersPlaced}
                student={this.state.student}
                // customerNameCallbackCustomers={this.customerRenting}
              />
            )}
          />
        </div>
      </div>
    );
  }
}

export default withRouter(Home);
