import React, { Component } from "react";
import { Route, Redirect } from "react-router-dom";
import { withRouter } from "react-router";
// import * as API from "./helpers/API";
import Home from "./components/Home";
import Login from "./components/Login";
import "./App.css";
//import PropTypes from "prop-types";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false,
      user: {
        name: "dongo",
        // email: "dongo@dongo.com",
        password: "dongo"
        // passwordConfirmation: "dongo"
      }
    };
    this.onLogin = this.onLogin.bind(this);
    this.onLogout = this.onLogout.bind(this);
  }

  async onLogin(user) {
    await this.setState({ isLoggedIn: true, user: user });
    console.log(`${this.state.user.name} logged in`);
  }

  async onLogout() {
    await this.setState({
      isLoggedIn: false,
      user: {
        name: "",
        password: ""
      }
    });
    console.log("User logged out");
  }

  render() {
    return (
      <div className="App">
        <Route
          exact
          path="/"
          render={() =>
            this.state.isLoggedIn ? (
              <Redirect to="/home" />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
        <Route
          path="/home"
          render={() =>
            this.state.isLoggedIn ? (
              <Home onLogout={this.onLogout} user={this.state.user} />
            ) : (
              <Redirect to="/login" />
            )
          }
        />
        <Route
          exact
          path="/login"
          render={() =>
            !this.state.isLoggedIn ? (
              <Login loggedIn={this.state.loggedIn} onLogin={this.onLogin} />
            ) : (
              <Redirect to="/home" />
            )
          }
        />
      </div>
    );
  }
}

export default withRouter(App);
